// Parse WhatsApp message for Google Drive commands
// Defensive: always output a valid array of objects with a plain object as json property
let from = '';
let message = '';
if ($json && typeof $json === 'object') {
  from = typeof $json.From === 'string' ? $json.From : '';
  message = typeof $json.Body === 'string' ? $json.Body.trim() : '';
}
if (!message) {
  return [{ json: { error: 'No message received', from } }];
}
const msgUpper = message.toUpperCase();
let command = 'HELP';
let path = '';
let destination = '';
if (msgUpper.startsWith('LIST')) {
  command = 'LIST';
  if (message.length > 4) {
    path = message.substring(4).trim();
  }
  if (!path || path === '/') {
    path = '/';
  }
} else if (msgUpper.startsWith('DELETE ')) {
  command = 'DELETE';
  path = message.substring(7).trim();
} else if (msgUpper.startsWith('MOVE ')) {
  command = 'MOVE';
  const parts = message.substring(5).trim().split(' ');
  path = parts[0] || '';
  destination = parts[1] || '';
} else if (msgUpper.startsWith('SUMMARY ')) {
  command = 'SUMMARY';
  path = message.substring(8).trim();
} else if (msgUpper.includes('HELP')) {
  command = 'HELP';
}
const safeJson = {
  from: from.replace('whatsapp:', ''),
  originalMessage: message,
  command,
  path,
  destination,
  timestamp: new Date().toISOString()
};
return [{ json: safeJson }];

