  from = typeof data.from === 'string' ? data.from : '';
  originalMessage = typeof data.originalMessage === 'string' ? data.originalMessage : '';
  timestamp = typeof data.timestamp === 'string' ? data.timestamp : new Date().toISOString();
  if (!command) command = 'HELP';

  return {
    json: { command, path, destination, from, originalMessage, timestamp }
  };
});

