// Generate response using REAL API data or error handling
const command = $json.command || 'HELP';
const path = $json.path || '';
const destination = $json.destination || '';

let responseMessage = '';

// Check if we have real API response data
const hasApiData = $json.files || $json.choices || $json.id || $json.fileId;

switch (command) {
  case 'HELP':
    responseMessage = `üìÇ *Google Drive Assistant*

üîß *Commands:*
‚Ä¢ LIST /ProjectX - List files in folder
‚Ä¢ DELETE /ProjectX/report.pdf - Delete file
‚Ä¢ MOVE /ProjectX/report.pdf /Archive - Move file
‚Ä¢ SUMMARY /ProjectX - AI bullet digest

‚ö†Ô∏è *Safety:* DELETE requires confirmation
üîí *OAuth2:* Secure Google Drive access

üí° *Examples:*
‚Ä¢ "LIST /Documents"
‚Ä¢ "SUMMARY /Reports"`;
    break;

  case 'LIST':
    if (hasApiData && $json.files) {
      // Real Google Drive API response
      const files = $json.files;
      if (files.length === 0) {
        responseMessage = `üìÇ *Files in ${path}:*

üì≠ No files found in this location.

üí° Try a different folder path.`;
      } else {
        responseMessage = `üìÇ *Files in ${path}:*

`;
        files.forEach((file, index) => {
          const size = file.size ? Math.round(file.size / 1024) + 'KB' : 'Unknown size';
          const date = file.modifiedTime ? new Date(file.modifiedTime).toLocaleDateString() : 'Unknown date';
          const type = file.mimeType?.includes('folder') ? 'üìÅ' : 'üìÑ';
          responseMessage += `${index + 1}. ${file.name} (${size})\n   ${type} ${file.mimeType?.split('/')[1]?.toUpperCase() || 'FILE'} ‚Ä¢ üìÖ ${date}\n\n`;
        });
        responseMessage += `‚úÖ Found ${files.length} items`;
      }
    } else {
      // API call failed, show error
      responseMessage = `‚ùå *Google Drive Error*

Couldn't access Google Drive API.
Please check:
‚Ä¢ OAuth2 token validity
‚Ä¢ API permissions
‚Ä¢ Network connection

üîß Contact admin for setup.`;
    }
    break;

  case 'DELETE':
    if ($json.files && $json.files.length > 0) {
      responseMessage = `‚úÖ *File Deleted Successfully*

File: ${$json.files[0].name || path}
ID: ${$json.files[0].id}
Timestamp: ${new Date().toISOString()}

üóëÔ∏è File moved to trash.`;
    } else {
      responseMessage = `‚ùå *Delete Failed*

Couldn't delete: ${path}

Possible issues:
‚Ä¢ File not found
‚Ä¢ Permission denied
‚Ä¢ Invalid OAuth token

üõ°Ô∏è Safety: File preserved.`;
    }
    break;

  case 'MOVE':
    if ($json.files && $json.files.length > 0) {
      responseMessage = `‚úÖ *File Moved Successfully*

From: ${path}
To: ${destination}
File: ${$json.files[0].name}
Timestamp: ${new Date().toISOString()}

üìÅ File relocated.`;
    } else {
      responseMessage = `‚ùå *Move Failed*

Couldn't move: ${path}
To: ${destination}

Possible issues:
‚Ä¢ Source file not found
‚Ä¢ Destination folder invalid
‚Ä¢ Permission denied

üìÇ File remains in original location.`;
    }
    break;

  case 'SUMMARY':
    if (hasApiData && $json.choices) {
      // Real OpenAI API response
      const aiResponse = $json.choices[0]?.message?.content || 'No summary generated';
      responseMessage = `üìã *AI Summary for ${path}:*

${aiResponse}

ü§ñ Generated by OpenAI GPT-4o
üìä MIME-type aware content analysis`;
    } else {
      responseMessage = `‚ùå *AI Summary Failed*

Couldn't generate summary for: ${path}

Possible issues:
‚Ä¢ OpenAI API key invalid
‚Ä¢ Rate limit exceeded
‚Ä¢ Network timeout

ü§ñ Please try again later.`;
    }
    break;

  default:
    responseMessage = `‚ùì Unknown command: "${command}"

Send HELP for available commands.`;
}

return [{
  json: {
    toNumber: '918250110934',
    responseMessage: responseMessage,
    command: command,
    path: path,
    destination: destination,
    timestamp: new Date().toISOString(),
    hasRealData: hasApiData
  }
}];
